import React from 'react';
import styled from 'styled-components';
import { ReactComponent as Heart } from '../images/heart.svg';

const StyledFooter = styled.footer`
    width: 80%;
    margin: 5rem auto 0;
    padding: 20px 45px;
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
    position: relative;

    &:before {
        position: absolute;
        border-top: 1px solid #EBEBEB;
        width: 60%;
    }
`

const CreditContainer = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: flex-start;
    align-items: center;
`

const HeartText = styled.span`
    color: #EBEBEB;
`

const ListContainer = styled.ul`
    list-style: none;
    padding: 5px 0 0 0;
    color: #EBEBEB;
`

const ListElement = styled.li`
    display: inline-block;
    margin: 0 0 0 30px;
`

const ListAnchor = styled.a`
    transition: all .4s ease-in-out;
    text-decoration: none;
    color: currentColor;
    &:hover {
    color: #0CCD93;
    } 
`

const Footer = () => {
    return (
        <StyledFooter>
            <CreditContainer>
                <HeartText>Made with</HeartText>
                <Heart />
                <HeartText>Haig Douzy</HeartText>
            </CreditContainer>
            <ListContainer>
                <ListElement><ListAnchor href="#">Github</ListAnchor></ListElement>
                <ListElement><ListAnchor href="#">Linked-In</ListAnchor></ListElement>
            </ListContainer>
        </StyledFooter>
    )
}

export default Footer;
