import React from 'react';
import styled from 'styled-components';
import { ReactComponent as Heart } from '../images/heart.svg';

const StyledFooter = styled.footer`
    width: 75vw;
    margin: 0 auto;
    border-top: 1px solid rgba(255,255,255, 0.1);
`

const FooterContainer = styled.div`
    width: 40%;
    padding: 20px
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
`

const CreditContainer = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: flex-start;
    align-items: center;
`

const HeartContainer = styled.div`
    display: inline-block;
    padding: 0 3px;
    width: 12px;
    height: 12px;
`

const HeartText = styled.span`
    color: rgba(235,235,235, 0.85);
    font-size: 15px;
`

const ListContainer = styled.ul`
    list-style: none;
    padding: 5px 0 0 0;
    color: rgba(235,235,235, 0.85);
`

const ListElement = styled.li`
    display: inline-block;
    margin: 0 0 0 30px;
`

const ListAnchor = styled.a`
    transition: all .4s ease-in-out;
    text-decoration: none;
    color: currentColor;
    font-size: 15px;
    &:hover {
    color: #EBEBEB;
    } 
`

const Footer = () => {
    return (
        <StyledFooter>
            <FooterContainer>
                <CreditContainer>
                    <HeartText>Made with</HeartText>
                    <HeartContainer>
                        <Heart />
                    </HeartContainer>
                    <HeartText>Haig Douzy</HeartText>
                </CreditContainer>
                <ListContainer>
                    <ListElement><ListAnchor href="#">Github</ListAnchor></ListElement>
                    <ListElement><ListAnchor href="#">Linked-In</ListAnchor></ListElement>
                </ListContainer>
            </FooterContainer>
        </StyledFooter>
    )
}

export default Footer;
