import React from 'react';
import styled from 'styled-components';
import { ReactComponent as Heart } from '../images/heart.svg';

const StyledFooter = styled.footer`
    width: 80%;
    margin: 5rem auto 0;
    padding: 20px 45px;
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
`

const CreditContainer = styled.div`

`

const ListContainer = styled.ul`
  list-style: none;
  padding: 5px 0 0 0;
  color: #EBEBEB;
`

const ListElement = styled.li`
  display: inline-block;
  margin: 0 0 0 30px;
`

const ListAnchor = styled.a`
  transition: all .4s ease-in-out;
  text-decoration: none;
  color: currentColor;
  &:hover {
    color: #0CCD93;
  } 
`

const Footer = () => {
    return (
        <StyledFooter>
            <CreditContainer>

            </CreditContainer>
            <ListContainer>
                <ListElement><ListAnchor href="#">Github</ListAnchor></ListElement>
                <ListElement><ListAnchor href="#">Linked-In</ListAnchor></ListElement>
            </ListContainer>
        </StyledFooter>
    )
}

export default Footer;
