import React, { useState } from 'react';
import styled from 'styled-components';
import { ReactComponent as Arrow } from '../images/arrow-right.svg';

const ArrowContainer = styled.div`
    opacity: 0;
    width: 20px;
    height: 20px;
    position: absolute;
    right: 30px;
    bottom: 20px;
    transition: .3s ease-in-out;
`

const TileWrapper = styled.li`
    width: 250px;
    height: 150px;
    display: block;
    position: relative;
    margin: 5px;
    padding: 10px;
    border-radius: 2px;
    color: #EBEBEB;
    background-color: rgba(255, 255, 255, 0.05);
    &:hover {
        transition: transform .3s ease-in-out;
        -ms-transform: scale(1.025);
        -webkit-transform: scale(1.025);
        transform: scale(1.025);

        & > ${ArrowContainer} {
            transform: translate(5px, 0);
            opacity: 1;
            color: #0CCD93;
        }
    };
`

const TileHeading = styled.h2`
    margin: 0;
    font-size: 18px;
    margin: 10px 10px;
`

const TileDate = styled.p`
    font-weight: 300;
    margin: 10px 10px;
`

const TileSportType = styled.p`
    font-weight: 300;
    position: absolute;
    margin: 0 0 0 10px;
    bottom: 20px;
`

const Tile = () => {
    return (
        <TileWrapper>
            <TileHeading>
                Fullham Vs. Arsenal
            </TileHeading>
            <TileDate>
                @10:00pm
            </TileDate>
            <TileSportType>
                Football
            </TileSportType>
            <ArrowContainer>
                <Arrow />
            </ArrowContainer>
        </TileWrapper>
    )
}

export default Tile;
