import React, { useEffect, useState } from 'react';
import styled from 'styled-components';

  const EventItem = (props) => {
    const [eventData, setEventData] = useState('')

    useEffect(() => {
      fetch(`${props.api.secondary + props.item}`)
      .then(res => res.json())
      .then(result => {
        setEventData(result.events[0])
      })
      .catch(err => {
        console.log("Error: " + err)
      })
    }, []);

    return (
      <li key={props.index}>{eventData.name}</li>
    )
  }

const EventContent = () => {
    const api = {
        base: 'https://cors-anywhere.herokuapp.com/https://api.smarkets.com/v3/popular/event_ids/?limit=10&inplay_enabled=true',
        secondary: 'https://cors-anywhere.herokuapp.com/https://api.smarkets.com/v3/events/'
      }

    const [eventsList, setEventsList] = useState('')

    useEffect(() => {
        fetch(`${api.base}`)
        .then(res => res.json())
        .then(result => {
        setEventsList(result)
        })
        .catch(err => {
        console.log("Error: " + err)
        })
    }, []);

    return (
        <ul>
          {
            eventsList && eventsList.popular_event_ids.map((e, index) => <EventItem key={index} api={api} item={e} />)
          }
        </ul>
    )
}

export default EventContent;
