import React, { useEffect, useState } from 'react'
import styled from 'styled-components'
import { ReactComponent as ReactLogo } from './images/smarkets1.svg'

const Header = styled.header`
  max-width: 80%;
  margin: 0 auto;
  padding: 20px 45px;
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: center;
`

const LogoContainer = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: flex-start;
  align-items: center;
`

const LogoExtension = styled.h3`
  display: inline-block;
  padding: 4px 0 0 10px;
  color: #0CCD93;
`

const ListContainer = styled.ul`
  list-style: none;
  padding: 5px 0 0 0;
  color: #EBEBEB;
`

const ListElement = styled.li`
  display: inline-block;
  margin: 0 0 0 30px;
`

const ListAnchor = styled.a`
  transition: all .4s ease-in-out;
  text-decoration: none;
  color: currentColor;
  &:hover {
    color: #0CCD93;
  } 
`

const api = {
  base: 'https://cors-anywhere.herokuapp.com/https://api.smarkets.com/v3/popular/event_ids/?limit=10&inplay_enabled=true',
  secondary: 'https://cors-anywhere.herokuapp.com/https://api.smarkets.com/v3/events/'
}

const EventItem = (props) => {
  // const [eventData, setEventData] = useState('')
  console.log(props)

//   useEffect(() => {
//     fetch(`${api.secondary} + prop`)
//   })
  return (
    <li key={props.key}>{props.item}</li>
  )
}

const App = () => {
  const [eventsList, setEventsList] = useState('')

  useEffect(() => {
    fetch(`${api.base}`)
    .then(res => res.json())
    .then(result => {
      setEventsList(result)
    })
  }, []);


// const listItems = eventsList.popular_event_ids.map((e) => <li>{e}</li>)

  return (
    <>
      <Header>
          <LogoContainer>
            <ReactLogo />
            <LogoExtension>Simplified</LogoExtension>
          </LogoContainer>
        <ListContainer>
          <ListElement><ListAnchor href="#">Language</ListAnchor></ListElement>
          <ListElement><ListAnchor href="#">Settings</ListAnchor></ListElement>
          <ListElement><ListAnchor href="#">Profile</ListAnchor></ListElement>
        </ListContainer>
      </Header>
      <div>
        <ul>
          {
            // eventsList && eventsList.popular_event_ids.map((e) => <li key={e}>{e}</li>)
            eventsList && eventsList.popular_event_ids.map((e) => <EventItem item={e} />)
          }
        </ul>
      </div>
    </>
  );
}

export default App;
