import React from 'react';
import styled from 'styled-components';

const ModalStyle = styled.div`
    position:fixed;
    background: white;
    width: 80%;
    height: auto;
    top:50%;
    left:50%;
    transform: translate(-50%,-50%);
`

const Modal = (props) => {
    return (
        <ModalContainer display={props.modalState}>
            <ModalStyle>
                <button onClick={props.handleClose}>
                    Close
                </button>
            </ModalStyle>
        </ModalContainer>
    )
}

const ModalContainer = styled.div`
    position: fixed;
    top: 0;
    left: 0;
    width:100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.6);
    display: ${props.modalState};
`

export default Modal;
