import React, { useEffect, useState } from 'react'
import Header from './components/Header';

const api = {
  base: 'https://cors-anywhere.herokuapp.com/https://api.smarkets.com/v3/popular/event_ids/?limit=10&inplay_enabled=true',
  secondary: 'https://cors-anywhere.herokuapp.com/https://api.smarkets.com/v3/events/'
}

const EventItem = (props) => {
  const [eventData, setEventData] = useState('')

  useEffect(() => {
    fetch(`${api.secondary + props.item}`)
    .then(res => res.json())
    .then(result => {
      setEventData(result)
      console.log(result)
    })
    .catch(err => {
      console.log("Error: " + err)
    })
  }, []);
  return (
    <li key={props.key}>{eventData}</li>
  )
}

const App = () => {
  const [eventsList, setEventsList] = useState('')

  useEffect(() => {
    fetch(`${api.base}`)
    .then(res => res.json())
    .then(result => {
      setEventsList(result)
    })
    .catch(err => {
      console.log("Error: " + err)
    })
  }, []);


// const listItems = eventsList.popular_event_ids.map((e) => <li>{e}</li>)

  return (
    <>
    <Header />
      <div>
        <ul>
          {
            eventsList && eventsList.popular_event_ids.map((e, key) => <EventItem key={key} item={e} />)
          }
        </ul>
      </div>
    </>
  );
}

export default App;
