import React, { useState } from 'react';
import styled from 'styled-components';
import { ReactComponent as Arrow } from '../images/arrow-right.svg';
import Modal from './Modal';

const ArrowContainer = styled.div`
    opacity: 0;
    width: 20px;
    height: 20px;
    position: absolute;
    right: 30px;
    bottom: 20px;
    transition: .4s ease-in-out;
`

const TileHeading = styled.p`
    margin: 0;
    font-size: 20px;
    margin: 10px 10px;
    color: #EBEBEB;
`

const TileWrapper = styled.li`
    min-height: 200px;
    display: block;
    position: relative;
    margin: 10px;
    padding: 20px 40px 20px 30px;
    border-radius: 2px;
    color: #EBEBEB;
    background-color: rgba(255, 255, 255, 0.05);
    &:hover {
        transition: transform .4s ease-in-out;
        -ms-transform: scale(1.03);
        -webkit-transform: scale(1.03);
        transform: scale(1.03);
        cursor: pointer; 
        background-color: rgba(255, 255, 255, 0.1);

        & > ${ArrowContainer} {
            transform: translate(5px, 0);
            opacity: 1;
            color: #0CCD93;
        }
    };
`

const TileDate = styled.p`
    font-weight: 300;
    margin: 10px 10px;
`

const TileSportType = styled.p`
    font-weight: 300;
    position: absolute;
    margin: 0 0 0 10px;
    bottom: 20px;
`

const Tile = (props) => {
    const [modalState, setModalState] = useState(false)

    const modalOpen = () => {
        setModalState(true);
    }

    const modalClose = () => {
        setModalState(false);
    }

    // const formatDate = (s) => {
    //     var date = { year: 'numeric', month: 'long', day: 'numeric' };
    //     return new Date(s).toLocaleDateString([], date);
    // };

    // var name = props.data.name;
    // var date = formatDate(props.data.start_datetime)
    // var type = /[^_]*/.exec(props.data.type)[0] === "american" ? "american football" : /[^_]*/.exec(props.data.type)[0];

    return (
        <>
            <a href="javascript:;" onClick={e => this.modalOpen(e)}>
                <TileWrapper>
                    <TileHeading>
                        {/* {name} */}
                        name
                    </TileHeading>
                    <TileDate>
                        {/* {date} */}
                        date
                    </TileDate>
                    <TileSportType>
                    {/* {type} */}
                    type
                    </TileSportType>
                    <ArrowContainer>
                        <Arrow />
                    </ArrowContainer>
                </TileWrapper>
            </a>
            <Modal show={modalState} handleClose={e => this.modalClose(e)}>
                <p>Modal</p>
                <p>Data</p>
            </Modal>
        </>
    )
}

export default Tile;
