import React from 'react';
import styled from 'styled-components';

const StyledFooter = styled.footer`
    width: 80%;
    margin-top: 5rem;
    padding: 20px 45px;
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
`

const ListContainer = styled.ul`
  list-style: none;
  padding: 5px 0 0 0;
  color: #EBEBEB;
`

const ListElement = styled.li`
  display: inline-block;
  margin: 0 0 0 30px;
`

const ListAnchor = styled.a`
  transition: all .4s ease-in-out;
  text-decoration: none;
  color: currentColor;
  &:hover {
    color: #0CCD93;
  } 
`

const Footer = () => {
    return (
        <StyledFooter>
            <ListContainer>
                <ListElement><ListAnchor href="#">Language</ListAnchor></ListElement>
                <ListElement><ListAnchor href="#">Settings</ListAnchor></ListElement>
                <ListElement><ListAnchor href="#">Profile</ListAnchor></ListElement>
            </ListContainer>
        </StyledFooter>
    )
}

export default Footer;
