import React from 'react';
import styled from 'styled-components';

const ModalOverlay = styled.div`
    position: fixed;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    display: flex;
    align-items: center;
    justify-content: center;
    z-index: 98;
    height: 100%;
    width: 100%;
    background-color: rgba(0, 0, 0, 0.5);
    backdrop-filter: blur(3px);
`

const ModalContainer = styled.div`
    position: relative;
    z-index: 99;
    width: 100%;
    max-width: 20rem;
    max-height: 100%;
    margin: 0 auto;
`

const ModalClose = styled.button`
    position: absolute;
    /* right: 0; */
    left: 390px;
    /* padding: 10px 5px 0 10px; */
    border: 0;
    -webkit-appearance: none;
    background: none;
    color: #EBEBEB;
    cursor: pointer;
    transition: .4s ease-out;

    &:hover {
        color: #0CCD93
    }
`

const ModalBody = styled.div`
    width: 400px;
    height: 300px;
    background: #2b2a2d;
    border-radius: 6px;
    box-shadow: 0 0 0 1px rgba(0,0,0,0.1), 0 20px 60px 10px rgba(0,0,0,0.2);
`

const Modal = ({ children, show, setShow }, ...props) => {
    const res = show && (
        <ModalOverlay>
            <ModalContainer>
                <ModalClose onClick={() => setShow(false)}>
                    x
                </ModalClose>
                <ModalBody>{children}</ModalBody>
            </ModalContainer>
        </ModalOverlay>
    )

    return res;
}

export default Modal;
