import React, { useState } from 'react'
import styled from 'styled-components'
import { ReactComponent as ReactLogo } from './images/smarkets1.svg'

const Container = styled.div`
  background: #201f22;
  padding-bottom: 50px;
`

const Header = styled.header`
  margin: 0 auto;
  padding: 20px 45px;
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: center;
`

const LogoContainer = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: flex-start;
  align-items: center;
`

const LogoExtension = styled.h3`
  display: inline-block;
  padding: 4px 0 0 10px;
  color: #0CCD93;
`

const ListContainer = styled.ul`
  list-style: none;
  color: #EBEBEB;
`

const ListElement = styled.li`
  display: inline-block;
  margin-left: 30px;
`

const ListAnchor = styled.a`
  transition: all .25s ease-out;
  &:hover {
    color: #FFFFFF;
  } 
`

// const api = {
//   base: 'https://cors-anywhere.herokuapp.com/https://api.smarkets.com/v3/events/'
// }

const App = () => {
  // const [eventData, setEventData] = useState('')

  // fetch(`${api.base}`)
  //   .then(res => res.json())
  //   .then(result => {
  //     setEventData(result)
  //     console.log(result)
  //   })
  return (
    <Container>
      <Header>
          <LogoContainer>
            <ReactLogo />
            <LogoExtension>Simplified</LogoExtension>
          </LogoContainer>
        <ListContainer>
          <ListElement><ListAnchor href="#">Settings</ListAnchor></ListElement>
          <ListElement><ListAnchor href="#">Language</ListAnchor></ListElement>
          <ListElement><ListAnchor href="#">Profile</ListAnchor></ListElement>
        </ListContainer>
      </Header>
    </Container>
  );
}

export default App;
