import React from 'react';
import styled from 'styled-components';
import { ReactComponent as Logo } from '../images/smarkets1.svg';

const StyledHeader = styled.header`
  max-width: 80%;
  /* margin: 0 auto; */
  padding: 20px 45px;
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: center;
`

const LogoContainer = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: flex-start;
  align-items: center;
`

const LogoExtension = styled.h3`
  display: inline-block;
  padding: 4px 0 0 10px;
  color: #0CCD93;
`

const ListContainer = styled.ul`
  list-style: none;
  padding: 5px 0 0 0;
  color: #EBEBEB;
`

const ListElement = styled.li`
  display: inline-block;
  margin: 0 0 0 30px;
`

const ListAnchor = styled.a`
  transition: all .4s ease-in-out;
  text-decoration: none;
  color: currentColor;
  &:hover {
    color: #0CCD93;
  } 
`

const Header = () => {
    return (
        <StyledHeader>
            <LogoContainer>
                <Logo />
                <LogoExtension>Simplified</LogoExtension>
            </LogoContainer>
            <ListContainer>
                <ListElement><ListAnchor href="#">Language</ListAnchor></ListElement>
                <ListElement><ListAnchor href="#">Settings</ListAnchor></ListElement>
                <ListElement><ListAnchor href="#">Profile</ListAnchor></ListElement>
            </ListContainer>
        </StyledHeader>
    );
}

export default Header;
