import React, { useState } from 'react';
import styled from 'styled-components';
import { ReactComponent as Arrow } from '../images/arrow-right.svg';

// const Arrow = styled(ArrowSVG)`
//     display: none;
//     color: #0CCD93;
// `

const ArrowContainer = styled.div`
    display: none;
    width: 20px;
    height: 20px;
    position: absolute;
    right: 20px;
    bottom: 20px;
`

const TileContainer = styled.li`
    width: 250px;
    height: 150px;
    display: block;
    position: relative;
    margin: 5px;
    padding: 10px;
    border-radius: 2px;
    color: #EBEBEB;
    background-color: rgba(255, 255, 255, 0.05);
    /* box-shadow: 
    0px 0px 0px 1px rgba(0, 0, 0, 0.05),
    0px 4px 24px 0px rgba(0, 0, 0, 0.05), 
    0px 4px 8px 0px rgba(0, 0, 0, 0.05); */
    &:hover {
        transition: transform .3s;
        -ms-transform: scale(1.025);
        -webkit-transform: scale(1.025);
        transform: scale(1.025);

        & > ${ArrowContainer} {
            display: block;
            transition: all 1s ease-in-out;
        }
    };
`

const TileHeading = styled.h2`
    margin: 0;
    font-size: 18px;
    margin: 10px 10px;
`

const TileDate = styled.p`
    font-weight: 300;
    /* font-size: 14px; */
    margin: 10px 10px;
`

const TileSportType = styled.p`
    font-weight: 300;
    position: absolute;
    /* font-size: 14px; */
    margin: 0 0 0 10px;
    bottom: 20px;
`

const Tile = () => {
    return (
        <TileContainer>
            <TileHeading>
                Fullham Vs. Arsenal
            </TileHeading>
            <TileDate>
                @10:00pm
            </TileDate>
            <TileSportType>
                Football
            </TileSportType>
            <ArrowContainer>
                <Arrow />
            </ArrowContainer>
        </TileContainer>
    )
}

export default Tile;
