import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import Tile from './Tile';

const TileContainer = styled.ul`
    max-width: 80vw;
    list-style: none;
    margin: 3rem auto;
    padding: 0;
    display: flex;
    flex-direction: row;
    flex-wrap: wrap;
    justify-content: center;
    align-items: center;
`

const EventTitle = styled.div`
    color: #EBEBEB;
    margin: 3rem auto 5rem auto;
    font-size: 28px;
    text-align: center;
    line-height: 1.5;
`

const EventItem = (props) => {
    const [eventData, setEventData] = useState('')

    useEffect(() => {
      fetch(`${props.api.base + props.api.secondary + props.item}`)
      .then(res => res.json())
      .then(result => {
        setEventData(result.events[0])
      })
      .catch(err => {
        console.log("Error: " + err)
      })
      // eslint-disable-next-line
    }, []);

    return (
      <Tile data={eventData}></Tile>
    )
  }

const EventContent = () => {
    const api = {
        base: 'https://cors-anywhere.herokuapp.com/',
        primary: 'https://api.smarkets.com/v3/popular/event_ids/?limit=10&inplay_enabled=true',
        secondary: 'https://api.smarkets.com/v3/events/'
      }

    const [eventsList, setEventsList] = useState('')

    useEffect(() => {
        fetch(`${api.base + api.primary}`)
        .then(res => res.json())
        .then(result => {
        setEventsList(result)
        })
        .catch(err => {
        console.log("Error: " + err)
        })
        // eslint-disable-next-line
    }, []);

    return (
        <>
            <EventTitle>Welcome, these are today's top events!</EventTitle>
            <TileContainer>
                {
                eventsList && eventsList.popular_event_ids.map((e, key) => <EventItem key={key} api={api} item={e} />)
                }
                {/* <Tile />
                <Tile />
                <Tile />
                <Tile />
                <Tile />
                <Tile />
                <Tile />
                <Tile />
                <Tile />
                <Tile /> */}
            </TileContainer>
        </>
    )
}

export default EventContent;
