import React from 'react';
import styled from 'styled-components';

const ModalOverlay = styled.div`
    position: fixed;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    display: flex;
    align-items: center;
    justify-content: center;
    z-index: 98;
    height: 100%;
    width: 100%;
    background-color: rgba(0, 0, 0, 0.5);
    backdrop-filter: blur(3px);
`

const ModalContainer = styled.div`
    position: relative;
    z-index: 99;
    width: 100%;
    max-width: 20rem;
    max-height: 100%;
    margin: 0 auto;
`

const ModalClose = styled.button`
    position: absolute;
    top: 20px;
    left: 360px;
    font-size: 20px;
    border: 0;
    -webkit-appearance: none;
    background: none;
    color: #EBEBEB;
    cursor: pointer;
    transition: .4s ease-out;

    &:hover {
        color: #0CCD93
    }
`

const ModalBody = styled.div`
    width: 400px;
    height: 300px;
    background: #2b2a2d;
    border-radius: 6px;
    box-shadow: 0 0 0 1px rgba(0,0,0,0.1), 0 20px 60px 10px rgba(0,0,0,0.2);
`

const Header = styled.p`
    margin: 0;
    font-size: ${props => props.size};
    padding: 20px 30px 5px 20px;
    color: #EBEBEB;
    max-width: 75%;
    font-weight: ${props => props.weight};
    border-bottom: ${props => props.border};
`

const Children = styled.p`
    font-size: 16px;
    font-weight: 300;
    padding: 1px 0 0 40px;
    color: #EBEBEB;
`

const Modal = (props) => {
    const res = props.show && (
        <ModalOverlay>
            <ModalContainer>
                <ModalClose onClick={() => props.setShow(false)}>
                    x
                </ModalClose>
                <ModalBody>
                    <Header weight="700" size="20px" border="1px solid #EBEBEB">
                        More Info
                    </Header>
                    <Header weight="400" size="16px">
                        {props.name}
                    </Header>
                    <Children>
                        {props.league}
                    </Children>
                    <Children>
                        {props.date}
                    </Children>
                    <Children>
                        {props.time}
                    </Children>
                    <Children>
                        {props.item.short_name}
                    </Children>
                </ModalBody>
            </ModalContainer>
        </ModalOverlay>
    )

    return res;
}

export default Modal;
