import React, { useEffect, useState } from 'react';
import styled from 'styled-components';

const EventsListContainer = styled.ul`
    list-style: none;
    margin-top: 0;
    margin-bottom: 0;
    display: flex;
    flex-direction: row;
    flex-wrap: wrap;
    justify-content: center;
    align-items: center;
    /* display: grid;
    grid-gap: 20px;
    grid-template-columns: repeat(4, minmax(100px, 1fr)); */
`

const EventElement = styled.li`
    width: 250px;
    height: 150px;
    display: block;
    position: relative;
    margin: 5px;
    padding: 10px;
    border-radius: 2px;
    color: #EBEBEB;
    background-color: rgba(255, 255, 255, 0.05);
    /* box-shadow: 
    0px 0px 0px 1px rgba(0, 0, 0, 0.05),
    0px 4px 24px 0px rgba(0, 0, 0, 0.05), 
    0px 4px 8px 0px rgba(0, 0, 0, 0.05); */
    &:hover {
        transition: transform .3s;
        -ms-transform: scale(1.025);
        -webkit-transform: scale(1.025);
        transform: scale(1.025);
    }
`

const EventItem = (props) => {
    const [eventData, setEventData] = useState('')

    useEffect(() => {
      fetch(`${props.api.base + props.api.secondary + props.item}`)
      .then(res => res.json())
      .then(result => {
        setEventData(result.events[0])
      })
      .catch(err => {
        console.log("Error: " + err)
      })
    }, []);

    return (
      <EventElement>{eventData.name}</EventElement>
    )
  }

const EventContent = () => {
    const api = {
        base: 'https://cors-anywhere.herokuapp.com/',
        primary: 'https://api.smarkets.com/v3/popular/event_ids/?limit=10&inplay_enabled=true',
        secondary: 'https://api.smarkets.com/v3/events/'
      }

    const [eventsList, setEventsList] = useState('')

    useEffect(() => {
        fetch(`${api.base + api.primary}`)
        .then(res => res.json())
        .then(result => {
        setEventsList(result)
        })
        .catch(err => {
        console.log("Error: " + err)
        })
    }, []);

    return (
        <ul>
            <EventsListContainer>
                {
                eventsList && eventsList.popular_event_ids.map((e, key) => <EventItem key={key} api={api} item={e} />)
                }
                <EventItem></EventItem>
            </EventsListContainer>
        </ul>
    )
}

export default EventContent;
